import{_ as a,a as e,b as t,c as i}from"./controllability.513f4d45.js";import{_ as c}from"./plugin-vue_export-helper.21dcd24c.js";import{b as s,o as d,f as l}from"./framework.a1b98fa0.js";const k='{"title":"Design","description":"","frontmatter":{"title":"Design","lang":"en-US"},"headers":[{"level":2,"title":"Consistency","slug":"consistency"},{"level":2,"title":"Feedback","slug":"feedback"},{"level":2,"title":"Efficiency","slug":"efficiency"},{"level":2,"title":"Controllability","slug":"controllability"}],"relativePath":"en-US/guide/design.md","lastUpdated":1640934300003}',n={},r=l('<h1 id="design-disciplines" tabindex="-1" data-v-7e21cb3a>Design Disciplines <a class="header-anchor" href="#design-disciplines" aria-hidden="true" data-v-7e21cb3a>#</a></h1><div class="guide__design" data-v-7e21cb3a><div class="el-row cards" style="margin-left:-7px;margin-right:-7px;" data-v-7e21cb3a><div class="el-col el-col-24 el-col-xs-12 el-col-sm-6 is-guttered" data-v-7e21cb3a><div class="card" data-v-7e21cb3a><img src="'+a+'" alt="Consistency" data-v-7e21cb3a><p data-v-7e21cb3a>Consistency</p></div></div><div class="el-col el-col-24 el-col-xs-12 el-col-sm-6 is-guttered" data-v-7e21cb3a><div class="card" data-v-7e21cb3a><img src="'+e+'" alt="Feedback" data-v-7e21cb3a><p data-v-7e21cb3a>Feedback</p></div></div><div class="el-col el-col-24 el-col-xs-12 el-col-sm-6 is-guttered" data-v-7e21cb3a><div class="card" data-v-7e21cb3a><img src="'+t+'" alt="Efficiency" data-v-7e21cb3a><p data-v-7e21cb3a>Efficiency</p></div></div><div class="el-col el-col-24 el-col-xs-12 el-col-sm-6 is-guttered" data-v-7e21cb3a><div class="card" data-v-7e21cb3a><img src="'+i+'" alt="Controllability" data-v-7e21cb3a><p data-v-7e21cb3a>Controllability</p></div></div></div></div><h2 id="consistency" tabindex="-1" data-v-7e21cb3a>Consistency <a class="header-anchor" href="#consistency" aria-hidden="true" data-v-7e21cb3a>#</a></h2><ul data-v-7e21cb3a><li data-v-7e21cb3a><p data-v-7e21cb3a><strong data-v-7e21cb3a>Consistent with real life:</strong> in line with the process and logic of real life, and comply with languages and habits that the users are used to.</p></li><li data-v-7e21cb3a><p data-v-7e21cb3a><strong data-v-7e21cb3a>Consistent within interface:</strong> all elements should be consistent, such as: design style, icons and texts, position of elements, etc.</p></li></ul><h2 id="feedback" tabindex="-1" data-v-7e21cb3a>Feedback <a class="header-anchor" href="#feedback" aria-hidden="true" data-v-7e21cb3a>#</a></h2><ul data-v-7e21cb3a><li data-v-7e21cb3a><p data-v-7e21cb3a><strong data-v-7e21cb3a>Operation feedback:</strong> enable the users to clearly perceive their operations by style updates and interactive effects.</p></li><li data-v-7e21cb3a><p data-v-7e21cb3a><strong data-v-7e21cb3a>Visual feedback:</strong> reflect current state by updating or rearranging elements of the page.</p></li></ul><h2 id="efficiency" tabindex="-1" data-v-7e21cb3a>Efficiency <a class="header-anchor" href="#efficiency" aria-hidden="true" data-v-7e21cb3a>#</a></h2><ul data-v-7e21cb3a><li data-v-7e21cb3a><p data-v-7e21cb3a><strong data-v-7e21cb3a>Simplify the process:</strong> keep operating process simple and intuitive.</p></li><li data-v-7e21cb3a><p data-v-7e21cb3a><strong data-v-7e21cb3a>Definite and clear:</strong> enunciate your intentions clearly so that the users can quickly understand and make decisions.</p></li><li data-v-7e21cb3a><p data-v-7e21cb3a><strong data-v-7e21cb3a>Easy to identify:</strong> the interface should be straightforward, which helps the users to identify and frees them from memorizing and recalling.</p></li></ul><h2 id="controllability" tabindex="-1" data-v-7e21cb3a>Controllability <a class="header-anchor" href="#controllability" aria-hidden="true" data-v-7e21cb3a>#</a></h2><ul data-v-7e21cb3a><li data-v-7e21cb3a><p data-v-7e21cb3a><strong data-v-7e21cb3a>Decision making:</strong> giving advice about operations is acceptable, but do not make decisions for the users.</p></li><li data-v-7e21cb3a><p data-v-7e21cb3a><strong data-v-7e21cb3a>Controlled consequences:</strong> users should be granted the freedom to operate, including canceling, aborting or terminating current operation.</p></li></ul>',10),o=[r];function b(v,p,g,h,u,f){return d(),s("div",null,o)}var x=c(n,[["render",b],["__scopeId","data-v-7e21cb3a"]]);export{k as __pageData,x as default};
