import{_ as a,a as e,b as t,c as d}from"./chunks/controllability.279ef5b1.js";import{_ as i,c as s,o as l,e as n}from"./app.0e064402.js";const b='{"title":"Design","description":"","frontmatter":{"title":"Design","lang":"en-US"},"headers":[{"level":2,"title":"Consistency","slug":"consistency"},{"level":2,"title":"Feedback","slug":"feedback"},{"level":2,"title":"Efficiency","slug":"efficiency"},{"level":2,"title":"Controllability","slug":"controllability"}],"relativePath":"en-US/guide/design.md"}',r={},c=n('<h1 id="design-disciplines" tabindex="-1" data-v-3d2f428a>Design Disciplines <a class="header-anchor" href="#design-disciplines" aria-hidden="true" data-v-3d2f428a>#</a></h1><div class="guide__design" data-v-3d2f428a><div class="el-row cards" style="margin-left:-7px;margin-right:-7px;" data-v-3d2f428a><div class="el-col el-col-24 el-col-xs-12 el-col-sm-6 is-guttered" data-v-3d2f428a><div class="card" data-v-3d2f428a><img src="'+a+'" alt="Consistency" data-v-3d2f428a><p data-v-3d2f428a>Consistency</p></div></div><div class="el-col el-col-24 el-col-xs-12 el-col-sm-6 is-guttered" data-v-3d2f428a><div class="card" data-v-3d2f428a><img src="'+e+'" alt="Feedback" data-v-3d2f428a><p data-v-3d2f428a>Feedback</p></div></div><div class="el-col el-col-24 el-col-xs-12 el-col-sm-6 is-guttered" data-v-3d2f428a><div class="card" data-v-3d2f428a><img src="'+t+'" alt="Efficiency" data-v-3d2f428a><p data-v-3d2f428a>Efficiency</p></div></div><div class="el-col el-col-24 el-col-xs-12 el-col-sm-6 is-guttered" data-v-3d2f428a><div class="card" data-v-3d2f428a><img src="'+d+'" alt="Controllability" data-v-3d2f428a><p data-v-3d2f428a>Controllability</p></div></div></div></div><h2 id="consistency" tabindex="-1" data-v-3d2f428a>Consistency <a class="header-anchor" href="#consistency" aria-hidden="true" data-v-3d2f428a>#</a></h2><ul data-v-3d2f428a><li data-v-3d2f428a><p data-v-3d2f428a><strong data-v-3d2f428a>Consistent with real life:</strong> in line with the process and logic of real life, and comply with languages and habits that the users are used to.</p></li><li data-v-3d2f428a><p data-v-3d2f428a><strong data-v-3d2f428a>Consistent within interface:</strong> all elements should be consistent, such as: design style, icons and texts, position of elements, etc.</p></li></ul><h2 id="feedback" tabindex="-1" data-v-3d2f428a>Feedback <a class="header-anchor" href="#feedback" aria-hidden="true" data-v-3d2f428a>#</a></h2><ul data-v-3d2f428a><li data-v-3d2f428a><p data-v-3d2f428a><strong data-v-3d2f428a>Operation feedback:</strong> enable the users to clearly perceive their operations by style updates and interactive effects.</p></li><li data-v-3d2f428a><p data-v-3d2f428a><strong data-v-3d2f428a>Visual feedback:</strong> reflect current state by updating or rearranging elements of the page.</p></li></ul><h2 id="efficiency" tabindex="-1" data-v-3d2f428a>Efficiency <a class="header-anchor" href="#efficiency" aria-hidden="true" data-v-3d2f428a>#</a></h2><ul data-v-3d2f428a><li data-v-3d2f428a><p data-v-3d2f428a><strong data-v-3d2f428a>Simplify the process:</strong> keep operating process simple and intuitive.</p></li><li data-v-3d2f428a><p data-v-3d2f428a><strong data-v-3d2f428a>Definite and clear:</strong> enunciate your intentions clearly so that the users can quickly understand and make decisions.</p></li><li data-v-3d2f428a><p data-v-3d2f428a><strong data-v-3d2f428a>Easy to identify:</strong> the interface should be straightforward, which helps the users to identify and frees them from memorizing and recalling.</p></li></ul><h2 id="controllability" tabindex="-1" data-v-3d2f428a>Controllability <a class="header-anchor" href="#controllability" aria-hidden="true" data-v-3d2f428a>#</a></h2><ul data-v-3d2f428a><li data-v-3d2f428a><p data-v-3d2f428a><strong data-v-3d2f428a>Decision making:</strong> giving advice about operations is acceptable, but do not make decisions for the users.</p></li><li data-v-3d2f428a><p data-v-3d2f428a><strong data-v-3d2f428a>Controlled consequences:</strong> users should be granted the freedom to operate, including canceling, aborting or terminating current operation.</p></li></ul>',10),o=[c];function f(v,p,g,h,u,_){return l(),s("div",null,o)}var k=i(r,[["render",f],["__scopeId","data-v-3d2f428a"]]);export{b as __pageData,k as default};
