import{_ as a,a as e,b as t,c as i}from"./controllability.513f4d45.js";import{_ as c}from"./plugin-vue_export-helper.21dcd24c.js";import{b as s,o as d,f as l}from"./framework.a1b98fa0.js";const k='{"title":"Design","description":"","frontmatter":{"title":"Design","lang":"en-US"},"headers":[{"level":2,"title":"Consistency","slug":"consistency"},{"level":2,"title":"Feedback","slug":"feedback"},{"level":2,"title":"Efficiency","slug":"efficiency"},{"level":2,"title":"Controllability","slug":"controllability"}],"relativePath":"en-US/guide/design.md","lastUpdated":1640381350733}',n={},r=l('<h1 id="design-disciplines" tabindex="-1" data-v-b4c040f0>Design Disciplines <a class="header-anchor" href="#design-disciplines" aria-hidden="true" data-v-b4c040f0>#</a></h1><div class="guide__design" data-v-b4c040f0><div class="el-row cards" style="margin-left:-7px;margin-right:-7px;" data-v-b4c040f0><div class="el-col el-col-24 el-col-xs-12 el-col-sm-6 is-guttered" data-v-b4c040f0><div class="card" data-v-b4c040f0><img src="'+a+'" alt="Consistency" data-v-b4c040f0><p data-v-b4c040f0>Consistency</p></div></div><div class="el-col el-col-24 el-col-xs-12 el-col-sm-6 is-guttered" data-v-b4c040f0><div class="card" data-v-b4c040f0><img src="'+e+'" alt="Feedback" data-v-b4c040f0><p data-v-b4c040f0>Feedback</p></div></div><div class="el-col el-col-24 el-col-xs-12 el-col-sm-6 is-guttered" data-v-b4c040f0><div class="card" data-v-b4c040f0><img src="'+t+'" alt="Efficiency" data-v-b4c040f0><p data-v-b4c040f0>Efficiency</p></div></div><div class="el-col el-col-24 el-col-xs-12 el-col-sm-6 is-guttered" data-v-b4c040f0><div class="card" data-v-b4c040f0><img src="'+i+'" alt="Controllability" data-v-b4c040f0><p data-v-b4c040f0>Controllability</p></div></div></div></div><h2 id="consistency" tabindex="-1" data-v-b4c040f0>Consistency <a class="header-anchor" href="#consistency" aria-hidden="true" data-v-b4c040f0>#</a></h2><ul data-v-b4c040f0><li data-v-b4c040f0><p data-v-b4c040f0><strong data-v-b4c040f0>Consistent with real life:</strong> in line with the process and logic of real life, and comply with languages and habits that the users are used to.</p></li><li data-v-b4c040f0><p data-v-b4c040f0><strong data-v-b4c040f0>Consistent within interface:</strong> all elements should be consistent, such as: design style, icons and texts, position of elements, etc.</p></li></ul><h2 id="feedback" tabindex="-1" data-v-b4c040f0>Feedback <a class="header-anchor" href="#feedback" aria-hidden="true" data-v-b4c040f0>#</a></h2><ul data-v-b4c040f0><li data-v-b4c040f0><p data-v-b4c040f0><strong data-v-b4c040f0>Operation feedback:</strong> enable the users to clearly perceive their operations by style updates and interactive effects.</p></li><li data-v-b4c040f0><p data-v-b4c040f0><strong data-v-b4c040f0>Visual feedback:</strong> reflect current state by updating or rearranging elements of the page.</p></li></ul><h2 id="efficiency" tabindex="-1" data-v-b4c040f0>Efficiency <a class="header-anchor" href="#efficiency" aria-hidden="true" data-v-b4c040f0>#</a></h2><ul data-v-b4c040f0><li data-v-b4c040f0><p data-v-b4c040f0><strong data-v-b4c040f0>Simplify the process:</strong> keep operating process simple and intuitive.</p></li><li data-v-b4c040f0><p data-v-b4c040f0><strong data-v-b4c040f0>Definite and clear:</strong> enunciate your intentions clearly so that the users can quickly understand and make decisions.</p></li><li data-v-b4c040f0><p data-v-b4c040f0><strong data-v-b4c040f0>Easy to identify:</strong> the interface should be straightforward, which helps the users to identify and frees them from memorizing and recalling.</p></li></ul><h2 id="controllability" tabindex="-1" data-v-b4c040f0>Controllability <a class="header-anchor" href="#controllability" aria-hidden="true" data-v-b4c040f0>#</a></h2><ul data-v-b4c040f0><li data-v-b4c040f0><p data-v-b4c040f0><strong data-v-b4c040f0>Decision making:</strong> giving advice about operations is acceptable, but do not make decisions for the users.</p></li><li data-v-b4c040f0><p data-v-b4c040f0><strong data-v-b4c040f0>Controlled consequences:</strong> users should be granted the freedom to operate, including canceling, aborting or terminating current operation.</p></li></ul>',10),o=[r];function f(b,v,p,g,h,u){return d(),s("div",null,o)}var x=c(n,[["render",f],["__scopeId","data-v-b4c040f0"]]);export{k as __pageData,x as default};
