import{_ as a}from"./chunks/design-guide.oK7tYwpT.js";import{c as o,y as n,a as e,X as s,aG as t,o as l}from"./chunks/framework.D_9QPN7t.js";const r=e("h1",{id:"directrices-de-diseno",tabindex:"-1"},[s("Directrices de Diseño "),e("a",{class:"header-anchor vp-link",href:"#directrices-de-diseno","aria-label":'Permalink to "Directrices de Diseño"'},"​")],-1),c=t('<h2 id="consistencia" tabindex="-1">Consistencia <a class="header-anchor vp-link" href="#consistencia" aria-label="Permalink to &quot;Consistencia&quot;">​</a></h2><ul><li><p><strong>Consistencia con la vida real:</strong> En línea con la lógica de la vida real y cumpliendo con el lenguaje y los hábitos a los que están acostumbrados los usuarios.</p></li><li><p><strong>Consistencia desde el interior de la interfaz:</strong> Todos los elementos deben ser consistentes en estilo de diseño, iconos y textos, posición de elementos, etc.</p></li></ul><h2 id="retroalimentacion-feedback" tabindex="-1">Retroalimentación (Feedback) <a class="header-anchor vp-link" href="#retroalimentacion-feedback" aria-label="Permalink to &quot;Retroalimentación (Feedback)&quot;">​</a></h2><ul><li><p><strong>Feedback Operativo:</strong> Permite a los usuarios identificar claramente sus acciones gracias a las actualizaciones de estilo y efectos interactivos.</p></li><li><p><strong>Feedback visual:</strong> Refleja el estado actual delcontenido actualizando o reorganizando elementos de la página.</p></li></ul><h2 id="eficiencia" tabindex="-1">Eficiencia <a class="header-anchor vp-link" href="#eficiencia" aria-label="Permalink to &quot;Eficiencia&quot;">​</a></h2><ul><li><p><strong>Simplificar el proceso:</strong> Mantener el proceso operativo simple e intuitivo.</p></li><li><p><strong>Claro y Preciso:</strong> Exponer un ainterfaz clara para que los usuarios puedan entender y tomar decisiones rápidamente.</p></li><li><p><strong>Fácil de entender:</strong> La interfaz debe ser directa, sin excesivos pasos intermedios, lo que ayuda a los usuarios a identificar y los libera de memorizar y retirar.</p></li></ul><h2 id="controlabilidad" tabindex="-1">Controlabilidad <a class="header-anchor vp-link" href="#controlabilidad" aria-label="Permalink to &quot;Controlabilidad&quot;">​</a></h2><ul><li><p><strong>La toma de decisiones:</strong> Dar consejos sobre las opciones disponibles es aceptable, pero no tomar decisiones por los usuarios.</p></li><li><p><strong>Consecuencias controladas:</strong> Los usuarios deben tener la libertad de operar pudiendo errar, incluyendo cancelar, abortar o terminar la operación actual.</p></li></ul>',8),_=JSON.parse('{"title":"Design","description":"","frontmatter":{"title":"Design","lang":"es-ES"},"headers":[{"level":2,"title":"Consistencia","slug":"consistencia","link":"#consistencia","children":[]},{"level":2,"title":"Retroalimentación (Feedback)","slug":"retroalimentacion-feedback","link":"#retroalimentacion-feedback","children":[]},{"level":2,"title":"Eficiencia","slug":"eficiencia","link":"#eficiencia","children":[]},{"level":2,"title":"Controlabilidad","slug":"controlabilidad","link":"#controlabilidad","children":[]}],"relativePath":"es-ES/guide/design.md","filePath":"es-ES/guide/design.md","lastUpdated":null}'),d={name:"es-ES/guide/design.md"},h=Object.assign(d,{setup(u){return(p,m)=>{const i=a;return l(),o("div",null,[r,n(i),c])}}});export{_ as __pageData,h as default};
