import{_ as a,a as e,b as t,c as d}from"./controllability.513f4d45.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";import{b as s,o as l,f as n}from"./framework.a1b98fa0.js";const k='{"title":"Design","description":"","frontmatter":{"title":"Design","lang":"en-US"},"headers":[{"level":2,"title":"Consistency","slug":"consistency"},{"level":2,"title":"Feedback","slug":"feedback"},{"level":2,"title":"Efficiency","slug":"efficiency"},{"level":2,"title":"Controllability","slug":"controllability"}],"relativePath":"ru-RU/guide/design.md","lastUpdated":1640580485738}',r={},o=n('<h1 id="design-disciplines" tabindex="-1" data-v-32f08aad>Design Disciplines <a class="header-anchor" href="#design-disciplines" aria-hidden="true" data-v-32f08aad>#</a></h1><div class="guide__design" data-v-32f08aad><div class="el-row cards" style="margin-left:-7px;margin-right:-7px;" data-v-32f08aad><div class="el-col el-col-24 el-col-xs-12 el-col-sm-6 is-guttered" data-v-32f08aad><div class="card" data-v-32f08aad><img src="'+a+'" alt="Consistency" data-v-32f08aad><p data-v-32f08aad>Consistency</p></div></div><div class="el-col el-col-24 el-col-xs-12 el-col-sm-6 is-guttered" data-v-32f08aad><div class="card" data-v-32f08aad><img src="'+e+'" alt="Feedback" data-v-32f08aad><p data-v-32f08aad>Feedback</p></div></div><div class="el-col el-col-24 el-col-xs-12 el-col-sm-6 is-guttered" data-v-32f08aad><div class="card" data-v-32f08aad><img src="'+t+'" alt="Efficiency" data-v-32f08aad><p data-v-32f08aad>Efficiency</p></div></div><div class="el-col el-col-24 el-col-xs-12 el-col-sm-6 is-guttered" data-v-32f08aad><div class="card" data-v-32f08aad><img src="'+d+'" alt="Controllability" data-v-32f08aad><p data-v-32f08aad>Controllability</p></div></div></div></div><h2 id="consistency" tabindex="-1" data-v-32f08aad>Consistency <a class="header-anchor" href="#consistency" aria-hidden="true" data-v-32f08aad>#</a></h2><ul data-v-32f08aad><li data-v-32f08aad><p data-v-32f08aad><strong data-v-32f08aad>Consistent with real life:</strong> in line with the process and logic of real life, and comply with languages and habits that the users are used to.</p></li><li data-v-32f08aad><p data-v-32f08aad><strong data-v-32f08aad>Consistent within interface:</strong> all elements should be consistent, such as: design style, icons and texts, position of elements, etc.</p></li></ul><h2 id="feedback" tabindex="-1" data-v-32f08aad>Feedback <a class="header-anchor" href="#feedback" aria-hidden="true" data-v-32f08aad>#</a></h2><ul data-v-32f08aad><li data-v-32f08aad><p data-v-32f08aad><strong data-v-32f08aad>Operation feedback:</strong> enable the users to clearly perceive their operations by style updates and interactive effects.</p></li><li data-v-32f08aad><p data-v-32f08aad><strong data-v-32f08aad>Visual feedback:</strong> reflect current state by updating or rearranging elements of the page.</p></li></ul><h2 id="efficiency" tabindex="-1" data-v-32f08aad>Efficiency <a class="header-anchor" href="#efficiency" aria-hidden="true" data-v-32f08aad>#</a></h2><ul data-v-32f08aad><li data-v-32f08aad><p data-v-32f08aad><strong data-v-32f08aad>Simplify the process:</strong> keep operating process simple and intuitive.</p></li><li data-v-32f08aad><p data-v-32f08aad><strong data-v-32f08aad>Definite and clear:</strong> enunciate your intentions clearly so that the users can quickly understand and make decisions.</p></li><li data-v-32f08aad><p data-v-32f08aad><strong data-v-32f08aad>Easy to identify:</strong> the interface should be straightforward, which helps the users to identify and frees them from memorizing and recalling.</p></li></ul><h2 id="controllability" tabindex="-1" data-v-32f08aad>Controllability <a class="header-anchor" href="#controllability" aria-hidden="true" data-v-32f08aad>#</a></h2><ul data-v-32f08aad><li data-v-32f08aad><p data-v-32f08aad><strong data-v-32f08aad>Decision making:</strong> giving advice about operations is acceptable, but do not make decisions for the users.</p></li><li data-v-32f08aad><p data-v-32f08aad><strong data-v-32f08aad>Controlled consequences:</strong> users should be granted the freedom to operate, including canceling, aborting or terminating current operation.</p></li></ul>',10),c=[o];function f(v,p,g,h,u,_){return l(),s("div",null,c)}var x=i(r,[["render",f],["__scopeId","data-v-32f08aad"]]);export{k as __pageData,x as default};
